import {useState, useEffect} from 'react';
import { useParams } from 'react-router-dom';


export default function ProductDetail() {
    const [product, setProduct] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const { id } = useParams();

    useEffect(() => {
        const fetchProduct = async () => {
            try {
                const baseUrl = process.env.REACT_APP_BASE_URL || 'http://localhost:5000'; // Default value for testing
                const url = `${baseUrl}/products/${id}`;
                console.log('Fetching product from:', url); // Log URL

                const res = await fetch(url);
                console.log('Response received:', res); // Log the response

                if (!res.ok) {
                    const errorText = await res.text(); // Read the response text
                    console.error('Response error:', errorText); // Log the error text
                    throw new Error(`Network response was not ok: ${res.statusText}`);
                }

                const data = await res.json();
                console.log('Fetched data:', data); // Log fetched data

                if (data.success && data.product) {
                    setProduct(data.product);
                    console.log('Product state after setting:', data.product); // Log state after setting
                } else {
                    console.error('API response does not have success or product:', data); // Log unexpected API response
                    throw new Error('Product not found');
                }
            } catch (error) {
                console.error('Fetch error:', error); // Log fetch error
                setError(error.message);
            } finally {
                setLoading(false);
                console.log('Loading state set to false'); // Log loading state
            }
        };

        fetchProduct();
    }, [id]);

    if (loading) {
        console.log('Loading...'); // Log loading state
        return <div>Loading...</div>;
    }

    if (error) {
        console.log('Error:', error); // Log error state
        return <div>Error: {error}</div>;
    }

    if (!product) {
        console.log('No product found'); // Log no product state
        return <div>No product found</div>;
    }

    console.log('Product state:', product); // Log final product state

    return  product && <div className="container container-fluid">
                <div className="row f-flex justify-content-around">
                    <div className="col-12 col-lg-5 img-fluid" id="product_image">
                        <img src={product.images[0].image} alt="sdf" height="500" width="500" />
                    </div>

                    <div className="col-12 col-lg-5 mt-5">
                        <h3>{product.name}</h3>
                        <p id="product_id">Product #{product._id}</p>

                        <hr />

                        <div className="rating-outer">
                            <div className="rating-inner" style={{width : `${product.ratings/5 * 100}%`}}></div>
                        </div>
                

                        <hr />

                        <p id="product_price">${product.price}</p>
                        <div className="stockCounter d-inline">
                            <span className="btn btn-danger minus" >-</span>

                            <input type="number" className="form-control count d-inline" />

                            <span className="btn btn-primary plus" >+</span>
                        </div>
                        <button type="button" disabled={product.stock == 0}   id="cart_btn" className="btn btn-primary d-inline ml-4">Add to Cart</button>

                        <hr />

                        <p>Status: <span id="stock_status" className={product.stock > 0 ?'text-success':'text-danger'}>{product.stock > 0  ?'In Stock' : 'Out of Stock'}</span></p>

                        <hr />

                        <h4 className="mt-2">Description:</h4>
                        <p>{product.description}</p>
                        <hr />
                        <p id="product_seller mb-3">Sold by: <strong>{product.seller}</strong></p>
                        
                        <div className="rating w-50"></div>
                                
                    </div>

                </div>
            </div>
}
